import React, { Component } from "react"
import Countries from "./Counrty"
import "./index.css"

class Index extends Component {
  state = { country: [] }
  fetchApi = () => {
    let countryApi = "https://restcountries.eu/rest/v2/all"
    fetch(countryApi)
      .then(function(res) {
        return res.json()
      })
      .then(data => {
        this.setState({ country: data })
      })
  }
  sortByCountrie = () => {
    conutry=this
    this.state.country.sort((a, b) => {
      if (a.name > b.name) {
        return -1
      } else if (a.name < b.name) {
        return 1
      } else {
        return 0
      }
    })
  }
  componentWillMount() {
    this.fetchApi()
  }
  render() {
    console.log(this.state)

    return (
      <div className="container">
        <div className="wrapper">
          <div className="headtext">LIST OF COUNTRIES</div>
          <div className="searchbar">
            <input
              type="text"
              placeholder="Please type the country you want"
              className="inputValue"
            />
            <input type="button" value="Search" />
          </div>
        </div>
        <div className="countryHeading">
          <div>
            Name of Countries
            <a
              className="sortCountries"
              id="sortDownCountries"
              onClick={this.sortByCountrie}
            >
              &darr;
            </a>
            <a className="sortCountries" id="sortUpCountries">
              &uarr;
            </a>
          </div>
          <div>
            {" "}
            Capital City
            <a className="sortCapital" id="sortDownCapital">
              &darr;
            </a>
            <a className="sortCapital" id="sortUpCapital">
              &uarr;
            </a>
          </div>
          <div>
            Continent Name
            <a className="SortContinent" id="sortDownContinent">
              &darr;
            </a>
            <a className="SortContinent" id="sortUpContinent">
              &uarr;
            </a>
          </div>
          <div>
            Population
            <a className="sortPopulation" id="sortDownPopulation">
              &darr;
            </a>
            <a className="sortPopulation" id="sortUpPopulation">
              &uarr;
            </a>
          </div>
          <div>
            Flag
            <a id="sortArrow" />
          </div>
        </div>
        <div className="mainContent">
          {this.state.country.length > 0 ? (
            this.state.country.map(item => (
              <Countries
                name={item.name}
                capital={item.capital}
                continent={item.region}
                population={item.population}
                flag={item.flag}
              />
            ))
          ) : (
            <p>Loading data</p>
          )}
        </div>
      </div>
    )
  }
}
export default Index
